//Array in java -> it is used to store multiple values in a single variable

public class Araay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] mobile= {"Apple","Samsung","Huwaei","Oppo","Vivo","Xiaomi","One Plus","Lava","Realme"};
		System.out.println(mobile.length);
		
		for(int i=0;i<mobile.length;i++) {
			System.out.println(mobile[i]);
		}

	}

}
